import React from 'react';

const COLORS = ['black', 'red', 'blue', 'green'];

function Toolbar({ color, width, onColorChange, onWidthChange }) {
  return (
    <div style={{ display: 'flex', gap: 16, alignItems: 'center', marginBottom: 8 }}>
      <div>
        Color:
        {COLORS.map(c => (
          <button
            key={c}
            style={{ background: c, color: c === 'black' ? '#fff' : '#000', border: color === c ? '2px solid #333' : '1px solid #ccc', marginLeft: 4, width: 24, height: 24, borderRadius: '50%' }}
            onClick={() => onColorChange(c)}
          />
        ))}
      </div>
      <div>
        Width:
        <input
          type="range"
          min={2}
          max={16}
          value={width}
          onChange={e => onWidthChange(Number(e.target.value))}
          style={{ marginLeft: 8 }}
        />
        <span style={{ marginLeft: 8 }}>{width}px</span>
      </div>
    </div>
  );
}

export default Toolbar;
