import React from 'react';

const COLORS = ['black', 'red', 'blue', 'green'];

function Toolbar({ color, width, onColorChange, onWidthChange }) {
  return (
    <div style={{ display: 'flex', gap: 32, alignItems: 'center', marginBottom: 16, background: '#f7fafc', borderRadius: 8, padding: '12px 24px', boxShadow: '0 2px 8px rgba(49,130,206,0.06)' }}>
      <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
        <span style={{ fontWeight: 500, color: '#2d3748', marginRight: 8 }}>Color:</span>
        {COLORS.map(c => (
          <button
            key={c}
            style={{ background: c, color: c === 'black' ? '#fff' : '#000', border: color === c ? '2px solid #3182ce' : '1px solid #cbd5e1', marginLeft: 0, width: 28, height: 28, borderRadius: '50%', cursor: 'pointer', outline: 'none', boxShadow: color === c ? '0 0 0 2px #90cdf4' : 'none', transition: 'border 0.2s, box-shadow 0.2s' }}
            onClick={() => onColorChange(c)}
          />
        ))}
      </div>
      <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
        <span style={{ fontWeight: 500, color: '#2d3748' }}>Width:</span>
        <input
          type="range"
          min={2}
          max={16}
          value={width}
          onChange={e => onWidthChange(Number(e.target.value))}
          style={{ marginLeft: 8 }}
        />
        <span style={{ marginLeft: 8, fontWeight: 500, color: '#3182ce', minWidth: 32 }}>{width}px</span>
      </div>
    </div>
  );
}

export default Toolbar;
