import React, { useState, useRef } from 'react';
import DrawingCanvas from './DrawingCanvas';
import Toolbar from './Toolbar';

function Whiteboard({ roomId, onLeave }) {
  const [color, setColor] = useState('black');
  const [width, setWidth] = useState(4);
  const [userCount, setUserCount] = useState(1);
  const [copied, setCopied] = useState(false);
  const drawingCanvasRef = useRef();

  // Listen for user-count events from DrawingCanvas
  const handleUserCount = (count) => setUserCount(count);

  // Leave room handler
  const handleLeaveRoom = () => {
    if (drawingCanvasRef.current && drawingCanvasRef.current.leaveRoom) {
      drawingCanvasRef.current.leaveRoom();
    }
    if (onLeave) onLeave();
  };

  // Clear handler
  const handleClear = () => {
    if (drawingCanvasRef.current && drawingCanvasRef.current.clearCanvas) {
      drawingCanvasRef.current.clearCanvas();
    }
  };

  // Copy room code
  const handleCopyRoomCode = () => {
    navigator.clipboard.writeText(roomId);
    setCopied(true);
    setTimeout(() => setCopied(false), 1200);
  };

  return (
    <div style={{ minHeight: '100vh', background: 'linear-gradient(135deg, #f8fafc 0%, #e0e7ef 100%)', display: 'flex', alignItems: 'center', justifyContent: 'center', padding: 24 }}>
      <div style={{ background: '#fff', borderRadius: 16, boxShadow: '0 4px 24px rgba(0,0,0,0.08)', padding: 32, maxWidth: 950, width: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <div style={{ display: 'flex', flexWrap: 'wrap', alignItems: 'center', justifyContent: 'center', gap: 16, width: '100%', marginBottom: 8 }}>
          <h2 style={{ margin: 0, fontWeight: 700, fontSize: 28, letterSpacing: 1, color: '#2d3748' }}>
            Room: <span style={{ color: '#3182ce' }}>{roomId}</span>
          </h2>
          <button onClick={handleCopyRoomCode} style={{ marginLeft: 8, background: '#edf2f7', border: 'none', color: '#3182ce', fontWeight: 600, padding: '6px 16px', borderRadius: 6, fontSize: 15, cursor: 'pointer', boxShadow: '0 1px 4px rgba(49,130,206,0.08)', transition: 'background 0.2s' }}>
            {copied ? 'Copied!' : 'Copy Code'}
          </button>
          <span style={{ marginLeft: 16, background: '#3182ce', color: '#fff', borderRadius: 12, padding: '4px 14px', fontWeight: 600, fontSize: 15, boxShadow: '0 1px 4px rgba(49,130,206,0.10)' }}>
            {userCount} user{userCount !== 1 ? 's' : ''}
          </span>
        </div>
        <Toolbar color={color} width={width} onColorChange={setColor} onWidthChange={setWidth} onClear={handleClear} responsive />
        <div style={{ margin: '16px 0', width: '100%', display: 'flex', justifyContent: 'center' }}>
          <DrawingCanvas ref={drawingCanvasRef} roomId={roomId} color={color} width={width} onUserCount={handleUserCount} />
        </div>
        <button onClick={handleLeaveRoom} style={{ marginTop: 16, background: '#edf2f7', border: 'none', color: '#2d3748', fontWeight: 600, padding: '10px 32px', borderRadius: 8, fontSize: 16, cursor: 'pointer', boxShadow: '0 2px 8px rgba(49,130,206,0.08)', transition: 'background 0.2s' }}>Leave Room</button>
      </div>
    </div>
  );
}

export default Whiteboard;
