import React from 'react';

const COLORS = ['black', 'red', 'blue', 'green'];

function Toolbar({ color, width, onColorChange, onWidthChange, onClear }) {
  return (
    <div className="fixed top-0 left-0 right-0 z-10 bg-white shadow-md rounded-b-lg p-4 flex items-center justify-between mx-auto max-w-3xl">
      <div className="flex items-center gap-2">
        <span className="text-sm font-medium text-gray-700">Color:</span>
        {COLORS.map((c) => (
          <button
            key={c}
            className={`w-6 h-6 rounded-full border ${color === c ? 'border-blue-500 ring-2 ring-blue-300' : 'border-gray-300'} transition-all`}
            style={{ backgroundColor: c }}
            onClick={() => onColorChange(c)}
          />
        ))}
      </div>
      <div className="flex items-center gap-2">
        <span className="text-sm font-medium text-gray-700">Width:</span>
        <input
          type="range"
          min={2}
          max={16}
          value={width}
          onChange={(e) => onWidthChange(Number(e.target.value))}
          className="w-24 accent-blue-500"
        />
        <span className="text-sm font-medium text-blue-600">{width}px</span>
      </div>
      <button
        onClick={onClear}
        className="bg-red-500 text-white text-sm font-semibold px-4 py-2 rounded-lg hover:bg-red-600 transition-colors"
      >
        Clear
      </button>
    </div>
  );
}

export default Toolbar;
