import React, { useState, useRef } from 'react';
import DrawingCanvas from './DrawingCanvas';
import Toolbar from './Toolbar';

function Whiteboard({ roomId, onLeave }) {
  const [color, setColor] = useState('black');
  const [width, setWidth] = useState(4);
  const [userCount, setUserCount] = useState(1);
  const drawingCanvasRef = useRef();

  // Listen for user-count events from DrawingCanvas
  const handleUserCount = (count) => setUserCount(count);

  // Leave room handler
  const handleLeaveRoom = () => {
    if (drawingCanvasRef.current && drawingCanvasRef.current.leaveRoom) {
      drawingCanvasRef.current.leaveRoom();
    }
    if (onLeave) onLeave();
  };

  // Clear handler
  const handleClear = () => {
    if (drawingCanvasRef.current && drawingCanvasRef.current.clearCanvas) {
      drawingCanvasRef.current.clearCanvas();
    }
  };

  return (
    <div style={{ minHeight: '100vh', background: 'linear-gradient(135deg, #f8fafc 0%, #e0e7ef 100%)', display: 'flex', alignItems: 'center', justifyContent: 'center', padding: 12 }}>
      <div style={{ background: '#fff', borderRadius: 16, boxShadow: '0 4px 24px rgba(0,0,0,0.08)', padding: '5vw 2vw', maxWidth: 1000, width: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <h2 style={{ margin: 0, marginBottom: 8, fontWeight: 700, fontSize: 28, letterSpacing: 1, color: '#2d3748', textAlign: 'center' }}>Room: <span style={{ color: '#3182ce' }}>{roomId}</span></h2>
        <div style={{ marginBottom: 16, fontWeight: 'bold', color: '#4a5568', fontSize: 16, textAlign: 'center' }}>Active users: <span style={{ color: '#3182ce' }}>{userCount}</span></div>
        <Toolbar color={color} width={width} onColorChange={setColor} onWidthChange={setWidth} onClear={handleClear} />
        <div style={{ margin: '16px 0', width: '100%', display: 'flex', justifyContent: 'center', overflowX: 'auto' }}>
          <div style={{ minWidth: 320, maxWidth: '100vw', width: 800 }}>
            <DrawingCanvas ref={drawingCanvasRef} roomId={roomId} color={color} width={width} onUserCount={handleUserCount} />
          </div>
        </div>
        <button onClick={handleLeaveRoom} style={{ marginTop: 16, background: '#edf2f7', border: 'none', color: '#2d3748', fontWeight: 600, padding: '10px 32px', borderRadius: 8, fontSize: 16, cursor: 'pointer', boxShadow: '0 2px 8px rgba(49,130,206,0.08)', transition: 'background 0.2s' }}>Leave Room</button>
        <style>{`
          @media (max-width: 900px) {
            .whiteboard-canvas-container { width: 100vw !important; min-width: 0 !important; }
            .whiteboard-card { padding: 4vw 1vw !important; }
          }
          @media (max-width: 600px) {
            .whiteboard-card { padding: 2vw 0.5vw !important; }
            h2 { font-size: 20px !important; }
          }
        `}</style>
      </div>
    </div>
  );
}

export default Whiteboard;
