import React, { useRef, useEffect, useState } from 'react';
import io from 'socket.io-client';

const SOCKET_URL = process.env.REACT_APP_SOCKET_URL || 'http://localhost:5000';

function DrawingCanvas({ roomId, color, width, onClear }) {
  const canvasRef = useRef(null);
  const [socket, setSocket] = useState(null);
  const [drawing, setDrawing] = useState(false);
  const [lastPoint, setLastPoint] = useState(null);
  // Track lastPoint for each remote user
  const remoteLastPoints = useRef({});

  // Setup socket
  useEffect(() => {
    const s = io(SOCKET_URL);
    setSocket(s);
    s.emit('join-room', { roomId });
    s.on('init-drawing', (drawingData) => {
      redrawFromData(drawingData);
    });
    s.on('draw-start', handleRemoteDrawStart);
    s.on('draw-move', handleRemoteDrawMove);
    s.on('draw-end', handleRemoteDrawEnd);
    s.on('clear-canvas', clearCanvas);
    return () => s.disconnect();
    // eslint-disable-next-line
  }, [roomId]);

  // Drawing handlers
  const startDraw = (x, y) => {
    setDrawing(true);
    setLastPoint({ x, y });
    if (socket) socket.emit('draw-start', { x, y, color, width });
  };
  const draw = (x, y) => {
    if (!drawing) return;
    drawLine(lastPoint.x, lastPoint.y, x, y, color, width);
    setLastPoint({ x, y });
    if (socket) socket.emit('draw-move', { x, y, color, width });
  };
  const endDraw = () => {
    setDrawing(false);
    setLastPoint(null);
    if (socket) socket.emit('draw-end', {});
  };

  // Mouse/touch events
  const handlePointerDown = (e) => {
    const { x, y } = getCanvasCoords(e);
    startDraw(x, y);
  };
  const handlePointerMove = (e) => {
    if (!drawing) return;
    const { x, y } = getCanvasCoords(e);
    draw(x, y);
  };
  const handlePointerUp = () => endDraw();

  // Helpers
  function getCanvasCoords(e) {
    const rect = canvasRef.current.getBoundingClientRect();
    const clientX = e.touches ? e.touches[0].clientX : e.clientX;
    const clientY = e.touches ? e.touches[0].clientY : e.clientY;
    return {
      x: clientX - rect.left,
      y: clientY - rect.top
    };
  }
  function drawLine(x1, y1, x2, y2, color, width) {
    const ctx = canvasRef.current.getContext('2d');
    ctx.strokeStyle = color;
    ctx.lineWidth = width;
    ctx.lineCap = 'round';
    ctx.beginPath();
    ctx.moveTo(x1, y1);
    ctx.lineTo(x2, y2);
    ctx.stroke();
  }
  function clearCanvas() {
    const ctx = canvasRef.current.getContext('2d');
    ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
    remoteLastPoints.current = {};
    if (onClear) onClear();
  }
  function redrawFromData(drawingData) {
    clearCanvas();
    let last = null;
    drawingData.forEach(cmd => {
      if (cmd.type === 'clear') {
        clearCanvas();
        last = null;
      } else if (cmd.type === 'stroke') {
        const { x, y, color, width } = cmd.data;
        if (last) drawLine(last.x, last.y, x, y, color, width);
        last = { x, y };
      }
    });
  }

  // Remote events (track per user)
  function handleRemoteDrawStart({ userId, x, y, color, width }) {
    if (!userId) return;
    remoteLastPoints.current[userId] = { x, y, color, width };
  }
  function handleRemoteDrawMove({ userId, x, y, color, width }) {
    if (!userId) return;
    const last = remoteLastPoints.current[userId];
    if (last) {
      drawLine(last.x, last.y, x, y, color, width);
    }
    remoteLastPoints.current[userId] = { x, y, color, width };
  }
  function handleRemoteDrawEnd({ userId }) {
    if (!userId) return;
    delete remoteLastPoints.current[userId];
  }

  // Clear button
  const handleClear = () => {
    if (socket) socket.emit('clear-canvas');
    clearCanvas();
  };

  return (
    <div style={{ position: 'relative' }}>
      <canvas
        ref={canvasRef}
        width={800}
        height={600}
        style={{ border: '1px solid #ccc', background: '#fff', touchAction: 'none' }}
        onMouseDown={handlePointerDown}
        onMouseMove={handlePointerMove}
        onMouseUp={handlePointerUp}
        onMouseLeave={handlePointerUp}
        onTouchStart={handlePointerDown}
        onTouchMove={handlePointerMove}
        onTouchEnd={handlePointerUp}
      />
      <button onClick={handleClear} style={{ position: 'absolute', top: 10, right: 10 }}>Clear</button>
    </div>
  );
}

export default DrawingCanvas;
