import React, { useState } from 'react';

function RoomJoin({ onJoin }) {
  const [roomCode, setRoomCode] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!roomCode.match(/^[a-zA-Z0-9]{6,8}$/)) {
      setError('Room code must be 6-8 alphanumeric characters.');
      return;
    }
    // Call backend to join/create room
    try {
      const res = await fetch('/api/rooms/join', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ roomId: roomCode })
      });
      if (res.ok) {
        onJoin(roomCode);
      } else {
        setError('Failed to join room.');
      }
    } catch (err) {
      setError('Server error.');
    }
  };

  return (
    <div style={{ minHeight: '100vh', background: 'linear-gradient(135deg, #f8fafc 0%, #e0e7ef 100%)', display: 'flex', alignItems: 'center', justifyContent: 'center', padding: 24 }}>
      <div style={{ background: '#fff', borderRadius: 16, boxShadow: '0 4px 24px rgba(0,0,0,0.08)', padding: 40, maxWidth: 400, width: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <h1 style={{ fontWeight: 800, fontSize: 32, color: '#2d3748', marginBottom: 8, letterSpacing: 1, textAlign: 'center' }}>Collaborative Whiteboard</h1>
        <p style={{ color: '#4a5568', fontSize: 16, marginBottom: 32, textAlign: 'center' }}>
          Enter a room code to join or create a collaborative whiteboard.<br />No login required!
        </p>
        <form onSubmit={handleSubmit} style={{ width: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center', gap: 16 }}>
          <input
            type="text"
            value={roomCode}
            onChange={e => setRoomCode(e.target.value)}
            placeholder="Enter room code"
            maxLength={8}
            style={{
              width: '100%',
              padding: '12px 16px',
              fontSize: 18,
              borderRadius: 8,
              border: '1px solid #cbd5e1',
              outline: 'none',
              marginBottom: 8,
              boxSizing: 'border-box',
              background: '#f7fafc',
              color: '#2d3748',
              fontWeight: 500
            }}
          />
          <button
            type="submit"
            style={{
              width: '100%',
              background: '#3182ce',
              color: '#fff',
              border: 'none',
              borderRadius: 8,
              padding: '12px 0',
              fontWeight: 700,
              fontSize: 18,
              cursor: 'pointer',
              boxShadow: '0 2px 8px rgba(49,130,206,0.08)',
              transition: 'background 0.2s'
            }}
          >
            Join Room
          </button>
        </form>
        {error && <div style={{ color: '#e53e3e', marginTop: 16, fontWeight: 600 }}>{error}</div>}
      </div>
    </div>
  );
}

export default RoomJoin;
