import React, { useState } from 'react';
import DrawingCanvas from './DrawingCanvas';
import Toolbar from './Toolbar';

function Whiteboard({ roomId }) {
  const [color, setColor] = useState('black');
  const [width, setWidth] = useState(4);
  const [userCount, setUserCount] = useState(1);

  // Listen for user-count events from DrawingCanvas
  const handleUserCount = (count) => setUserCount(count);

  return (
    <div className="whiteboard">
      <h2>Whiteboard Room: {roomId}</h2>
      <div style={{ marginBottom: 8, fontWeight: 'bold' }}>Active users: {userCount}</div>
      <Toolbar color={color} width={width} onColorChange={setColor} onWidthChange={setWidth} />
      <DrawingCanvas roomId={roomId} color={color} width={width} onUserCount={handleUserCount} />
    </div>
  );
}

export default Whiteboard;
